package learning;

public class largestofthree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a,b,c;
		a=800;b=90990;c=90;
		
		if(a>b && a>c)
			System.out.println("a is greater     "+a);
		else if(b>c && b>a)
			System.out.println("b is greater     "+b);
		else
			System.out.println("c is greater     "+c);
			

	}

}
